mapper.update:
UPDATE spend_utility 
	SET
	  spend_utility_pay=#{spendUtilityPay}
	, spend_utility_contents=#{spendUtilityContents}
	, spend_utility_date=NOW()
WHERE spend_utility_code = #{spendUtilityCode};

mapper.select:
SELECT 
	 COUNT(*)
FROM spend_utility su, subject sub
WHERE su.subject_code = sub.subject_code AND contract_shop_code = #{contractShopCode}
ORDER BY spend_utility_date DESC;

		SELECT 
			 COUNT(*)
		FROM spend_utility su, subject sub
		WHERE su.subject_code = sub.subject_code AND contract_shop_code = #{contractShopCode}
		
		
        
            AND
                sub.${utilityKey} LIKE CONCAT('%',#{utilityValue},'%')
        
        
        	AND 
        		su.spend_utility_date between #{beginDate} and NOW()		
        
        
        	AND 
        		su.spend_utility_date  between '2000-01-01' and #{endDate}
        
        
        	AND 
        		su.spend_utility_date  between #{beginDate} and #{endDate}
        
        
		ORDER BY spend_utility_date DESC;

SELECT su.spend_utility_code ,su.subject_code ,su.contract_shop_code ,su.spend_utility_pay ,su.spend_utility_contents ,su.spend_utility_date ,sub.subject_name FROM spend_utility su, subject sub WHERE su.subject_code = sub.subject_code AND su.contract_shop_code = #{contractShopCode}
AND sub.${utilityKey} LIKE CONCAT('%',#{utilityValue},'%')
AND su.spend_utility_date between #{beginDate} and NOW()
AND su.spend_utility_date between '2000-01-01' and #{endDate}
AND su.spend_utility_date between #{beginDate} and #{endDate}
ORDER BY su.spend_utility_date DESC
LIMIT #{startRow},#{rowPerPage};
